import json
import requests
from django.core.files import File 
from django.conf import settings

class datasharing(object):
    def __init__(self):
        path=settings.MEDIA_ROOT + "data.json"

    def power(self):
        
        with open('UserInterface/files/data.json',"r") as f:
            datafile = json.load(f)
        TriTrackPrb= datafile["TriTrackPowerBoard"]
        f.close()

        return TriTrackPrb

    def mic(self):
        
        with open('UserInterface/files/data.json',"r") as f:
            datafile = json.load(f)
        TriTrackMic= datafile["TriTrackDataMic"]
        f.close()

        return TriTrackMic
    
    def rub(self): 
        
        with open('UserInterface/files/data.json',"r") as f:
            datafile = json.load(f)
        Rubbish= datafile["Rubbish"]
        f.close()

        return Rubbish